import React, { useState } from 'react';
import './App.css';
import cars from "./cars.json";
import 'bootstrap/dist/css/bootstrap.min.css'

function App() {
  const [searchTerm,setSearchTerm]=useState("");

  const [currentPage, setCurrentPage]= useState(1);
  const recordsPerPage = 6;
  const lastlndex = currentPage * recordsPerPage;
  const firstlndex = lastlndex - recordsPerPage;
  const records = cars.slice(firstlndex, lastlndex);
  const npage = Math.ceil(cars.length/recordsPerPage);
  const numbers = [...Array(npage + 1).keys()].slice(1)

  return (
    <>
      <div className="cars-container">
      <div className="navbar">
        <div className="seachInput-container">
          <input type="text" id='search' placeholder='Search Here' onChange={(event)=>{
            setSearchTerm(event.target.value);
          }}/>
        </div>
      </div>

        <div className="carDetails-container">
          {
            cars
            .filter((item)=>{
              if(searchTerm === ""){
                return item;
              }else if(item.title.toLowerCase().includes(searchTerm.toLowerCase())){
                return item ;
              }
            })
            .map((item)=>{
              return (
                <div className="template">
                  <img src={item.image} alt="" />
                  <h2 className='title'>{item.title}</h2>
                  <span className='start_production'>{item.start_production}</span>
                  <span className='class'>{item.class}</span>
                </div>
              )
            })
          }
        </div>
        <nav aria-label="...">
  <ul class="pagination">
    <li class="page-item disabled">
      <span class="page-link">Previous</span>
    </li>
    <li class="page-item"><a class="page-link" href="#">1</a></li>
    <li class="page-item active" aria-current="page">
      <span class="page-link">2</span>
    </li>
    <li class="page-item"><a class="page-link" href="#">3</a></li>
    <li class="page-item">
      <a class="page-link" href="#">Next</a>
    </li>
  </ul>
</nav>
      </div>
    </>
  );
}

export default App;
